import{_ as e,c as o,o as s,a as c}from"./app.9160966e.js";const _='{"title":"Access control list (ACL)","description":"","frontmatter":{},"headers":[{"level":2,"title":"Cache","slug":"cache"},{"level":3,"title":"Enabled","slug":"enabled"},{"level":3,"title":"Expires in","slug":"expires-in"},{"level":2,"title":"Roles","slug":"roles"},{"level":3,"title":"Superuser","slug":"superuser"}],"relativePath":"config/api/acl.md","lastUpdated":1642434740655}',i={},a=c('<h1 id="access-control-list-acl" tabindex="-1">Access control list (ACL) <a class="header-anchor" href="#access-control-list-acl" aria-hidden="true">#</a></h1><p>Path: <code>apps/api/src/config/acl.ts</code></p><p>Access control list (<code>ACL</code>) implementation is based on three models:</p><ul><li><code>user</code></li><li><code>role</code></li><li><code>permission</code></li></ul><p>Relations between the models are defined as follows:</p><ul><li><code>many-to-many</code> between <code>user</code> and <code>role</code></li><li><code>many-to-many</code> between <code>user</code> and <code>permission</code></li><li><code>many-to-many</code> between <code>role</code> and <code>permission</code></li></ul><p>This allows to grant permissions to users in two ways:</p><ul><li>directly associate <code>permission</code> with <code>user</code></li><li>associate <code>permission</code> with <code>user</code> through <code>role</code> (acting as grouping record for a set of permissions)</li></ul><p>Path: <code>src/config/acl.ts</code></p><h2 id="cache" tabindex="-1">Cache <a class="header-anchor" href="#cache" aria-hidden="true">#</a></h2><p>Controls the permissions / roles caching</p><h3 id="enabled" tabindex="-1">Enabled <a class="header-anchor" href="#enabled" aria-hidden="true">#</a></h3><p>Enables / disables caching for permissions / roles.</p><ul><li>object-path: <code>cache.enabled</code></li><li>dotenv var: <code>ACL_CACHE_ENABLED</code></li><li>type: <code>boolean</code></li><li>default: <code>false</code></li></ul><h3 id="expires-in" tabindex="-1">Expires in <a class="header-anchor" href="#expires-in" aria-hidden="true">#</a></h3><p>Sets time the cache data expires at. Defined as <code>ms-formatted</code> string (see <a href="https://github.com/vercel/ms" target="_blank" rel="noopener noreferrer">ms</a> for more information) or number in seconds.</p><ul><li>object-path: <code>cache.expiresIn</code></li><li>dotenv var: <code>ACL_CACHE_EXPIRES_IN</code></li><li>type: <code>number | string</code></li><li>default: <code>&#39;7d&#39;</code></li></ul><h2 id="roles" tabindex="-1">Roles <a class="header-anchor" href="#roles" aria-hidden="true">#</a></h2><p>List of special-purpose roles.</p><ul><li>object-path: <code>roles</code></li></ul><h3 id="superuser" tabindex="-1">Superuser <a class="header-anchor" href="#superuser" aria-hidden="true">#</a></h3><p>Any newly created permission will be associated with this role.</p><ul><li>object-path: <code>roles.superuser</code></li><li>type: <code>string</code></li><li>default: <code>&#39;superuser&#39;</code></li></ul>',23),l=[a];function r(d,t,n,p,h,u){return s(),o("div",null,l)}var f=e(i,[["render",r]]);export{_ as __pageData,f as default};
