import{_ as o,c as i,a2 as a,o as t}from"./chunks/framework.Nbmr5Z7t.js";const p=JSON.parse('{"title":"Security","description":"","frontmatter":{},"headers":[],"relativePath":"config/api/security.md","filePath":"config/api/security.md"}'),r={name:"config/api/security.md"};function l(c,e,d,n,s,h){return t(),i("div",null,e[0]||(e[0]=[a('<h1 id="security" tabindex="-1">Security <a class="header-anchor" href="#security" aria-label="Permalink to &quot;Security&quot;">​</a></h1><p>Path: <code>apps/api/src/config/security.ts</code></p><h2 id="cross-origin-resource-sharing-cors" tabindex="-1">Cross-origin resource sharing (CORS) <a class="header-anchor" href="#cross-origin-resource-sharing-cors" aria-label="Permalink to &quot;Cross-origin resource sharing (CORS)&quot;">​</a></h2><h3 id="cors-origin" tabindex="-1">CORS Origin <a class="header-anchor" href="#cors-origin" aria-label="Permalink to &quot;CORS Origin&quot;">​</a></h3><p>Single origin or list of origins to allow cross-origin resource sharing. Define these, when SPA applications (<code>Admin client</code> and <code>Survey client</code>) are deployed to different domain than <code>API server</code>.</p><ul><li>object-path: <code>cors.origin</code></li><li>dotenv var: <code>CORS_ORIGIN</code> (for list of origins, use <code>comma-delimited</code> string)</li><li>type: <code>string | string[] | boolean</code></li><li>default: <code>false</code></li></ul><h2 id="proxy" tabindex="-1">Proxy <a class="header-anchor" href="#proxy" aria-label="Permalink to &quot;Proxy&quot;">​</a></h2><p>List of trusted proxies. It allows to define list proxies if application is behind multiple reverse proxies / load balancers.</p><p>Since application is usually at least behind local reverse proxy, set as <code>loopback</code> (which translates to <code>127.0.0.1 / ::1</code>).</p><ul><li>object-path: <code>proxy</code></li><li>dotenv var: <code>PROXY</code> (<code>comma-delimited</code> string list)</li><li>type: <code>string[] | boolean</code></li><li>default: <code>false</code></li></ul><h2 id="json-web-tokens" tabindex="-1">JSON web tokens <a class="header-anchor" href="#json-web-tokens" aria-label="Permalink to &quot;JSON web tokens&quot;">​</a></h2><p>JSON web tokens (<code>JWT</code>) settings</p><h3 id="issuer" tabindex="-1">Issuer <a class="header-anchor" href="#issuer" aria-label="Permalink to &quot;Issuer&quot;">​</a></h3><p>Issuer claim for signing JWT tokens</p><ul><li>object-path: <code>jwt.issuer</code></li><li>dotenv var: <code>JWT_ISSUER</code></li><li>type: <code>string</code></li><li>default: <code>&#39;intake24&#39;</code></li></ul><h3 id="access-token-secret" tabindex="-1">Access token secret <a class="header-anchor" href="#access-token-secret" aria-label="Permalink to &quot;Access token secret&quot;">​</a></h3><p>Secret to sign JWT access token</p><ul><li>object-path: <code>jwt.access.secret</code></li><li>dotenv var: <code>JWT_ACCESS_SECRET</code></li><li>type: <code>string</code></li></ul><h3 id="admin-application-settings" tabindex="-1">Admin application settings <a class="header-anchor" href="#admin-application-settings" aria-label="Permalink to &quot;Admin application settings&quot;">​</a></h3><p>JWT settings for admin frontend application.</p><h4 id="access-token-audience" tabindex="-1">Access token audience <a class="header-anchor" href="#access-token-audience" aria-label="Permalink to &quot;Access token audience&quot;">​</a></h4><p>Audience claim to sign JWT access token</p><ul><li>object-path: <code>jwt.admin.access.audience</code></li><li>type: <code>string[]</code></li><li>default: <code>[&#39;admin&#39;, &#39;access&#39;]</code></li></ul><h4 id="access-token-lifetime" tabindex="-1">Access token lifetime <a class="header-anchor" href="#access-token-lifetime" aria-label="Permalink to &quot;Access token lifetime&quot;">​</a></h4><p>Lifetime of JWT access token. Defined as <code>ms-formatted</code> string, see <a href="https://github.com/vercel/ms" target="_blank" rel="noreferrer">ms</a> for more information.</p><ul><li>object-path: <code>jwt.admin.access.lifetime</code></li><li>dotenv var: <code>JWT_ADMIN_ACCESS_LIFETIME</code></li><li>type: <code>string</code></li><li>default: <code>&#39;15m&#39;</code></li></ul><h4 id="refresh-token-secret" tabindex="-1">Refresh token secret <a class="header-anchor" href="#refresh-token-secret" aria-label="Permalink to &quot;Refresh token secret&quot;">​</a></h4><p>Secret to sign JWT refresh token</p><ul><li>object-path: <code>jwt.admin.refresh.secret</code></li><li>dotenv var: <code>JWT_ADMIN_REFRESH_SECRET</code></li><li>type: <code>string</code></li></ul><h4 id="refresh-token-audience" tabindex="-1">Refresh token audience <a class="header-anchor" href="#refresh-token-audience" aria-label="Permalink to &quot;Refresh token audience&quot;">​</a></h4><p>Audience claim to sign JWT refresh token</p><ul><li>object-path: <code>jwt.admin.refresh.audience</code></li><li>type: <code>string[]</code></li><li>default: <code>[&#39;admin&#39;, &#39;refresh&#39;]</code></li></ul><h4 id="refresh-token-lifetime" tabindex="-1">Refresh token lifetime <a class="header-anchor" href="#refresh-token-lifetime" aria-label="Permalink to &quot;Refresh token lifetime&quot;">​</a></h4><p>Lifetime of JWT refresh token. Defined as <code>ms-formatted</code> string, see <a href="https://github.com/vercel/ms" target="_blank" rel="noreferrer">ms</a> for more information.</p><ul><li>object-path: <code>jwt.admin.refresh.lifetime</code></li><li>dotenv var: <code>JWT_ADMIN_REFRESH_LIFETIME</code></li><li>type: <code>string</code></li><li>default: <code>&#39;1d&#39;</code></li></ul><h4 id="jwt-refresh-token-cookie" tabindex="-1">JWT Refresh token cookie <a class="header-anchor" href="#jwt-refresh-token-cookie" aria-label="Permalink to &quot;JWT Refresh token cookie&quot;">​</a></h4><p>Refresh token is stored in <code>http-only</code> cookie in client&#39;s browser. There are several properties to configure refresh token cookie.</p><h4 id="cookie-name" tabindex="-1">Cookie name <a class="header-anchor" href="#cookie-name" aria-label="Permalink to &quot;Cookie name&quot;">​</a></h4><ul><li>object-path: <code>jwt.admin.cookie.name</code></li><li>type: <code>string</code></li><li>default: <code>it24a_refresh_token</code></li></ul><h4 id="cookie-maxage" tabindex="-1">Cookie maxAge <a class="header-anchor" href="#cookie-maxage" aria-label="Permalink to &quot;Cookie maxAge&quot;">​</a></h4><p>Lifetime of cookie. Defined as <code>ms-formatted</code> string, see <a href="https://github.com/vercel/ms" target="_blank" rel="noreferrer">ms</a> for more information.</p><ul><li>object-path: <code>jwt.admin.cookie.maxAge</code></li><li>default: <code>jwt.admin.refresh.lifetime</code> value</li><li>type: <code>string</code></li></ul><h4 id="cookie-httponly" tabindex="-1">Cookie httpOnly <a class="header-anchor" href="#cookie-httponly" aria-label="Permalink to &quot;Cookie httpOnly&quot;">​</a></h4><ul><li>object-path: <code>jwt.admin.cookie.httpOnly</code></li><li>type: <code>boolean</code></li><li>default: <code>true</code></li></ul><h4 id="cookie-path" tabindex="-1">Cookie path <a class="header-anchor" href="#cookie-path" aria-label="Permalink to &quot;Cookie path&quot;">​</a></h4><ul><li>object-path: <code>jwt.admin.cookie.path</code></li><li>dotenv var: <code>JWT_ADMIN_COOKIE_PATH</code></li><li>type: <code>string</code></li><li>default: <code>&#39;/api/admin/auth&#39;</code></li></ul><h4 id="cookie-samesite" tabindex="-1">Cookie sameSite <a class="header-anchor" href="#cookie-samesite" aria-label="Permalink to &quot;Cookie sameSite&quot;">​</a></h4><ul><li>object-path: <code>jwt.admin.cookie.sameSite</code></li><li>dotenv var: <code>JWT_ADMIN_COOKIE_SAMESITE</code></li><li>type: <code>boolean | &#39;lax&#39; | &#39;strict&#39; | &#39;none&#39;</code></li><li>default: <code>&#39;lax&#39;</code></li></ul><h4 id="cookie-secure" tabindex="-1">Cookie secure <a class="header-anchor" href="#cookie-secure" aria-label="Permalink to &quot;Cookie secure&quot;">​</a></h4><ul><li>object-path: <code>jwt.admin.cookie.secure</code></li><li>dotenv var: <code>JWT_ADMIN_COOKIE_SECURE</code></li><li>type: <code>boolean</code></li><li>default: <code>false</code></li></ul><h3 id="survey-application-settings" tabindex="-1">Survey application settings <a class="header-anchor" href="#survey-application-settings" aria-label="Permalink to &quot;Survey application settings&quot;">​</a></h3><p>JWT settings for survey frontend application.</p><h4 id="access-token-audience-1" tabindex="-1">Access token audience <a class="header-anchor" href="#access-token-audience-1" aria-label="Permalink to &quot;Access token audience&quot;">​</a></h4><p>Audience claim to sign JWT access token</p><ul><li>object-path: <code>jwt.survey.access.audience</code></li><li>type: <code>string[]</code></li><li>default: <code>[&#39;survey&#39;, &#39;access&#39;]</code></li></ul><h4 id="access-token-lifetime-1" tabindex="-1">Access token lifetime <a class="header-anchor" href="#access-token-lifetime-1" aria-label="Permalink to &quot;Access token lifetime&quot;">​</a></h4><p>Lifetime of JWT access token. Defined as <code>ms-formatted</code> string, see <a href="https://github.com/vercel/ms" target="_blank" rel="noreferrer">ms</a> for more information.</p><ul><li>object-path: <code>jwt.survey.access.lifetime</code></li><li>dotenv var: <code>JWT_SURVEY_ACCESS_LIFETIME</code></li><li>type: <code>string</code></li><li>default: <code>&#39;15m&#39;</code></li></ul><h4 id="refresh-token-secret-1" tabindex="-1">Refresh token secret <a class="header-anchor" href="#refresh-token-secret-1" aria-label="Permalink to &quot;Refresh token secret&quot;">​</a></h4><p>Secret to sign JWT refresh token</p><ul><li>object-path: <code>jwt.survey.refresh.secret</code></li><li>dotenv var: <code>JWT_SURVEY_REFRESH_SECRET</code></li><li>type: <code>string</code></li></ul><h4 id="refresh-token-audience-1" tabindex="-1">Refresh token audience <a class="header-anchor" href="#refresh-token-audience-1" aria-label="Permalink to &quot;Refresh token audience&quot;">​</a></h4><p>Audience claim to sign JWT refresh token</p><ul><li>object-path: <code>jwt.survey.refresh.audience</code></li><li>type: <code>string[]</code></li><li>default: <code>[&#39;survey&#39;, &#39;refresh&#39;]</code></li></ul><h4 id="refresh-token-lifetime-1" tabindex="-1">Refresh token lifetime <a class="header-anchor" href="#refresh-token-lifetime-1" aria-label="Permalink to &quot;Refresh token lifetime&quot;">​</a></h4><p>Lifetime of JWT refresh token. Defined as <code>ms-formatted</code> string, see <a href="https://github.com/vercel/ms" target="_blank" rel="noreferrer">ms</a> for more information.</p><ul><li>object-path: <code>jwt.survey.refresh.lifetime</code></li><li>dotenv var: <code>JWT_SURVEY_REFRESH_LIFETIME</code></li><li>type: <code>string</code></li><li>default: <code>&#39;1d&#39;</code></li></ul><h4 id="jwt-refresh-token-cookie-1" tabindex="-1">JWT Refresh token cookie <a class="header-anchor" href="#jwt-refresh-token-cookie-1" aria-label="Permalink to &quot;JWT Refresh token cookie&quot;">​</a></h4><p>Refresh token is stored in <code>http-only</code> cookie in client&#39;s browser. There are several properties to configure refresh token cookie.</p><h4 id="cookie-name-1" tabindex="-1">Cookie name <a class="header-anchor" href="#cookie-name-1" aria-label="Permalink to &quot;Cookie name&quot;">​</a></h4><ul><li>object-path: <code>jwt.survey.cookie.name</code></li><li>type: <code>string</code></li><li>default: <code>it24s_refresh_token</code></li></ul><h4 id="cookie-maxage-1" tabindex="-1">Cookie maxAge <a class="header-anchor" href="#cookie-maxage-1" aria-label="Permalink to &quot;Cookie maxAge&quot;">​</a></h4><p>Lifetime of cookie. Defined as <code>ms-formatted</code> string, see <a href="https://github.com/vercel/ms" target="_blank" rel="noreferrer">ms</a> for more information.</p><ul><li>object-path: <code>jwt.survey.cookie.maxAge</code></li><li>default: <code>jwt.survey.refresh.lifetime</code> value</li><li>type: <code>string</code></li></ul><h4 id="cookie-httponly-1" tabindex="-1">Cookie httpOnly <a class="header-anchor" href="#cookie-httponly-1" aria-label="Permalink to &quot;Cookie httpOnly&quot;">​</a></h4><ul><li>object-path: <code>jwt.survey.cookie.httpOnly</code></li><li>type: <code>boolean</code></li><li>default: <code>true</code></li></ul><h4 id="cookie-path-1" tabindex="-1">Cookie path <a class="header-anchor" href="#cookie-path-1" aria-label="Permalink to &quot;Cookie path&quot;">​</a></h4><ul><li>object-path: <code>jwt.survey.cookie.path</code></li><li>dotenv var: <code>JWT_SURVEY_COOKIE_PATH</code></li><li>type: <code>string</code></li><li>default: <code>&#39;/api/auth&#39;</code></li></ul><h4 id="cookie-samesite-1" tabindex="-1">Cookie sameSite <a class="header-anchor" href="#cookie-samesite-1" aria-label="Permalink to &quot;Cookie sameSite&quot;">​</a></h4><ul><li>object-path: <code>jwt.survey.cookie.sameSite</code></li><li>dotenv var: <code>JWT_SURVEY_COOKIE_SAMESITE</code></li><li>type: <code>boolean | &#39;lax&#39; | &#39;strict&#39; | &#39;none&#39;</code></li><li>default: <code>&#39;lax&#39;</code></li></ul><h4 id="cookie-secure-1" tabindex="-1">Cookie secure <a class="header-anchor" href="#cookie-secure-1" aria-label="Permalink to &quot;Cookie secure&quot;">​</a></h4><ul><li>object-path: <code>jwt.survey.cookie.secure</code></li><li>dotenv var: <code>JWT_SURVEY_COOKIE_SECURE</code></li><li>type: <code>boolean</code></li><li>default: <code>false</code></li></ul><h2 id="multi-factor-authentication-mfa" tabindex="-1">Multi-factor authentication (MFA) <a class="header-anchor" href="#multi-factor-authentication-mfa" aria-label="Permalink to &quot;Multi-factor authentication (MFA)&quot;">​</a></h2><p>System supports multi-factor authentication (<code>MFA</code>) for admin login.</p><p>Supported providers:</p><ul><li>OTP (one-time password)</li><li>WebAuthn (FIDO2)</li><li><a href="https://duo.com" target="_blank" rel="noreferrer">Duo Security</a></li></ul><p>User can add multiple MFA providers to their account.</p><h3 id="duo-provider-settings" tabindex="-1">Duo provider settings <a class="header-anchor" href="#duo-provider-settings" aria-label="Permalink to &quot;Duo provider settings&quot;">​</a></h3><p>For more information, check out <a href="https://duo.com/docs/duoweb" target="_blank" rel="noreferrer">duo&#39;s documentation for WebSDK</a>. Intake24 implements Universal Prompt flow.</p><h4 id="duo-client-id" tabindex="-1">Duo client ID <a class="header-anchor" href="#duo-client-id" aria-label="Permalink to &quot;Duo client ID&quot;">​</a></h4><p>Duo client ID - can be found in Duo administration</p><ul><li>object-path: <code>mfa.providers.duo.clientId</code></li><li>dotenv var: <code>DUO_CLIENT_ID</code></li><li>type: <code>string</code></li><li>default: <code>&#39;&#39;</code></li></ul><h4 id="duo-client-secret" tabindex="-1">Duo client secret <a class="header-anchor" href="#duo-client-secret" aria-label="Permalink to &quot;Duo client secret&quot;">​</a></h4><p>Duo client secret - can be found in Duo administration</p><ul><li>object-path: <code>mfa.providers.duo.clientSecret</code></li><li>dotenv var: <code>DUO_CLIENT_SECRET</code></li><li>type: <code>string</code></li><li>default: <code>&#39;&#39;</code></li></ul><h4 id="duo-api-host" tabindex="-1">Duo API host <a class="header-anchor" href="#duo-api-host" aria-label="Permalink to &quot;Duo API host&quot;">​</a></h4><p>Duo API hostname (e.g. api-a1b2c3d4e5.duosecurity.com) - can be found in Duo administration</p><ul><li>object-path: <code>mfa.providers.duo.apiHost</code></li><li>dotenv var: <code>DUO_API_HOST</code></li><li>type: <code>string</code></li><li>default: <code>&#39;&#39;</code></li></ul><h4 id="duo-redirect-url" tabindex="-1">Duo redirect URL <a class="header-anchor" href="#duo-redirect-url" aria-label="Permalink to &quot;Duo redirect URL&quot;">​</a></h4><p>URL where to redirect from MFA verification step. This should be admin tool login page.</p><ul><li>object-path: <code>mfa.providers.duo.redirectUrl</code></li><li>dotenv var: <code>DUO_REDIRECT_URL</code></li><li>type: <code>string</code></li><li>default: <code>&#39;&#39;</code></li></ul><h2 id="passwords" tabindex="-1">Passwords <a class="header-anchor" href="#passwords" aria-label="Permalink to &quot;Passwords&quot;">​</a></h2><p>Settings for password restore functionality.</p><h3 id="expire" tabindex="-1">Expire <a class="header-anchor" href="#expire" aria-label="Permalink to &quot;Expire&quot;">​</a></h3><p>Password reset link expiration in <strong>minutes</strong>.</p><ul><li>object-path: <code>passwords.expire</code></li><li>dotenv var: <code>PASSWORDS_EXPIRES_IN</code></li><li>type: <code>string</code></li><li>default: <code>&#39;1h&#39;</code></li></ul><h2 id="authentication-tokens" tabindex="-1">Authentication tokens <a class="header-anchor" href="#authentication-tokens" aria-label="Permalink to &quot;Authentication tokens&quot;">​</a></h2><p>Settings for generation of random authentication tokens.</p><h3 id="size" tabindex="-1">Size <a class="header-anchor" href="#size" aria-label="Permalink to &quot;Size&quot;">​</a></h3><p>Size of the tokens - string&#39;s length.</p><ul><li>object-path: <code>authTokens.size</code></li><li>dotenv var: <code>AUTH_TOKENS_SIZE</code></li><li>type: <code>number</code></li><li>default: <code>32</code></li></ul><h3 id="alphabet" tabindex="-1">Alphabet <a class="header-anchor" href="#alphabet" aria-label="Permalink to &quot;Alphabet&quot;">​</a></h3><p>String of custom alphabet - character set to be used for token generation.</p><ul><li>object-path: <code>authTokens.alphabet</code></li><li>dotenv var: <code>AUTH_TOKENS_ALPHABET</code></li><li>type: <code>string | null</code></li><li>default: <code>null</code></li></ul><h2 id="sign-in-logging" tabindex="-1">Sign-in logging <a class="header-anchor" href="#sign-in-logging" aria-label="Permalink to &quot;Sign-in logging&quot;">​</a></h2><p>Settings for sign-in logging.</p><h3 id="enabled" tabindex="-1">Enabled <a class="header-anchor" href="#enabled" aria-label="Permalink to &quot;Enabled&quot;">​</a></h3><p>Enable/disable database logging of sign-in attempts.</p><ul><li>object-path: <code>signInLog.enabled</code></li><li>dotenv var: <code>SIGN_IN_LOG_ENABLED</code></li><li>type: <code>boolean</code></li><li>default: <code>true</code></li></ul>',119)]))}const f=o(r,[["render",l]]);export{p as __pageData,f as default};
