import{_ as a,o as e,c as s,U as t}from"./chunks/framework.1eef7d9b.js";const g=JSON.parse('{"title":"Upgrade guide","description":"","frontmatter":{},"headers":[],"relativePath":"overview/upgrade.md","filePath":"overview/upgrade.md"}'),o={name:"overview/upgrade.md"},n=t(`<h1 id="upgrade-guide" tabindex="-1">Upgrade guide <a class="header-anchor" href="#upgrade-guide" aria-label="Permalink to &quot;Upgrade guide&quot;">​</a></h1><p>Intake24 V3 to V4 upgrade guide (WIP)</p><h2 id="database-migrations" tabindex="-1">Database migrations <a class="header-anchor" href="#database-migrations" aria-label="Permalink to &quot;Database migrations&quot;">​</a></h2><p>Use most up-to-date V3 <code>foods</code> and <code>system</code> databases to run the migrations.</p><h3 id="migrate-system-database" tabindex="-1">Migrate system database <a class="header-anchor" href="#migrate-system-database" aria-label="Permalink to &quot;Migrate system database&quot;">​</a></h3><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">pnpm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">db:system:migrate</span></span></code></pre></div><h3 id="migrate-foods-database" tabindex="-1">Migrate foods database <a class="header-anchor" href="#migrate-foods-database" aria-label="Permalink to &quot;Migrate foods database&quot;">​</a></h3><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">pnpm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">db:foods:migrate</span></span></code></pre></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Depending on size of the databases, migration process can take from seconds to minutes. Both databases are being upgraded to use int8 instead of int4, which takes most of the time.</p><p>If you run into query timeout issues, you will have to increase the limits in sequelize config file (<code>packages/db/sequelize/{foods|system}/config.js</code>).</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Run the migration in specified order:</p><ol><li>System database</li><li>Foods database</li></ol><p>Some of the system database migrations are using foods database data (e.g. feedback data conversion into feedback-schemes) and eventually V3 old tables are dropped. Running the migrations in wrong order will fail.</p></div><h3 id="seed-databases-with-relevant-data" tabindex="-1">Seed databases with relevant data <a class="header-anchor" href="#seed-databases-with-relevant-data" aria-label="Permalink to &quot;Seed databases with relevant data&quot;">​</a></h3><p>Some of V3 data are being moved to database. To get this data into the database, run relevant seeders.</p><h4 id="standard-units" tabindex="-1">Standard units <a class="header-anchor" href="#standard-units" aria-label="Permalink to &quot;Standard units&quot;">​</a></h4><p>Standard units are being moved from V3 translation files to database. To seed the database with V3 source code standard units, run the following command:</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">packages/db</span></span>
<span class="line"></span>
<span class="line"><span style="color:#FFCB6B;">pnpm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">sequelize</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">db:seed</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--seed</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">v3-standard-portions.js</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--options-path</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">sequelize/foods/options.js</span></span></code></pre></div>`,15),i=[n];function d(l,r,p,c,h,u){return e(),s("div",null,i)}const b=a(o,[["render",d]]);export{g as __pageData,b as default};
